import styled from 'styled-components';

const LoaderContainer = styled.div`
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  @media (min-width: 768px) {
    width: 100px;
    height: 100px;
    img {
      border-radius: 50%;
    }
  }

  img {
    width: 100%;
  }
`;

export { LoaderContainer };
