import styled from 'styled-components';

const StyledLocationsContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  flex-wrap: wrap;
  width: 100%;

  a {
    text-decoration: none;
    color: inherit;
  }

  @media (max-width: 414px) {
    flex-direction: column;
    a {
      width: 100%;
    }
  }
`;

const ErrorMessage = styled.p`
  color: red;
  font-weight: bold;
`;

const LocationDetailsContainer = styled.div`
  width: 100%;
  padding: 5px;
`;

export { StyledLocationsContainer, ErrorMessage, LocationDetailsContainer };
