export const adjustType = str => str.replace(' ', '_').toLowerCase();

export const getLocationImage = type => {
  if (LOCATION_TYPES[adjustType(type)]) {
    return LOCATION_TYPES[adjustType(type)];
  }
  return unknownTypeImage;
};
