import React from 'react';
import Link from 'next/link';
import { useQuery } from '@apollo/react-hooks';
import gql from 'graphql-tag';
import InfiniteScroll from 'react-infinite-scroller';

import { StyledLocationsContainer, ErrorMessage } from './styles';

import Loader from 'src/components/Loader';
import LocationItem from './LocationItem';
import { useInfiniteScroll } from 'src/hooks/useInfiniteScroll';

const LOCATIONS_QUERY = gql`
  query Locations($page: Int!) {
    locations(page: $page) {
      info {
        count
        pages
        next
        prev
      }
      results {
        name
        type
        id
      }
    }
  }
`;

const LocationsContainer = () => {
  const [currentPage, setCurrentPage] = React.useState(0);
  const { data, error, refetch, loading, fetchMore } = useQuery(
    LOCATIONS_QUERY,
    {
      variables: { page: currentPage }
    }
  );
  const [locations, setLocations] = React.useState([]);

  // React.useEffect(() => {
  //   setLocations(prevState =>
  //     prevState.concat((data && data.locations.results) || [])
  //   );
  // }, [data]);

  const loadMore = () => {
    if (data.locations.info.next) {
      // setCurrentPage(prev => (prev += 1));
      fetchMore({
        variables: {
          page: data.locations.info.next
        },
        updateQuery: (prev, { fetchMoreResult }) => {
          if (!fetchMoreResult) return prev;
          console.log(prev.locations, fetchMoreResult);
          console.log({
            ...prev.locations,
            info: { ...fetchMoreResult.locations.info },
            results: prev.locations.results.concat(
              fetchMoreResult.locations.results
            )
          });
          return {
            ...prev.locations,
            info: { ...fetchMoreResult.info },
            results: prev.locations.results.concat(
              fetchMoreResult.locations.results
            )
          };
        }
      });
      // setLocations(
      //   prevState => data && [...prevState, ...data.locations.results]
      // );
    }
    return;
  };

  // useInfiniteScroll(loadMore);

  return (
    <>
      {/* <StyledLocationsContainer> */}
      {error && <ErrorMessage />}
      {data && (
        <InfiniteScroll
          pageStart={data.locations.info.next - 1}
          loadMore={loadMore}
          hasMore={true || false}
          loader={<Loader />}
        >
          {data.locations.results.map(location => (
            <Link href={`/locations/${location.id}`} key={location.id}>
              <a>
                <LocationItem location={location} />
              </a>
            </Link>
          ))}
        </InfiniteScroll>
      )}

      {/* </StyledLocationsContainer> */}
      {/* {loading && <Loader />} */}
    </>
  );
};

export default LocationsContainer;
