import Link from 'next/link';
import styled from 'styled-components';

const LocationItemContainer = styled(Link)`
  box-shadow: 2px 2px 10px rgba(29, 99, 234, 0.48);
  @media (min-width: 768px) {
    width: 398px;
    height: 145px;
  }
  width: 100%;
  height: 145px;
`;

export { LocationItemContainer };
