import styled from 'styled-components';

const DetailsImage = styled.div`
  width: 95%;
  height: 414px;

  @media (min-width: 768px) {
    width: 396px;
  }

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`;

export { DetailsImage };
