import React from 'react';
import gql from 'graphql-tag';
import { useQuery } from '@apollo/react-hooks';

import {
  ResidentDetailsContainer,
  ResidentImage,
  ResidentInfo
} from './styles';
import { Title, Text } from '../LocationsContainer/LocationItem/styles';

const RESIDENT_QUERY = gql`
  query Character($id: ID!) {
    character(id: $id) {
      id
      image
      name
      status
      type
      origin {
        name
      }
    }
  }
`;

const ResidentDetails = ({ id }) => {
  const { data } = useQuery(RESIDENT_QUERY, {
    variables: { id }
  });
  return (
    <>
      {data && (
        <ResidentDetailsContainer>
          <ResidentImage>
            <img src={data.character.image} alt="" />
          </ResidentImage>
          <ResidentInfo>
            <Title>{data.character.name}</Title>
            <Text>{data.character.name}</Text>
            <Text>{data.character.type}</Text>
            <Title>Status:</Title>
            <Title>{data.character.status}</Title>
            <Title>Home Planet:</Title>
            <Title>{data.character.origin.name}</Title>
          </ResidentInfo>
        </ResidentDetailsContainer>
      )}
    </>
  );
};

export default ResidentDetails;
