import Link from 'next/link';
import styled from 'styled-components';

const LocationItemContainer = styled.div`
  box-shadow: 2px 2px 10px rgba(29, 99, 234, 0.48);
  background: #312a2a;
  width: 95%;
  height: 145px;
  margin: 11px;
  display: flex;
  align-items: center;

  @media (min-width: 768px) {
    width: 396px;
  }
`;

const LocationItemImage = styled.div`
  flex: 35%;
  height: 100%;

  img {
    object-fit: cover;
  }
`;

const LocationItemInfo = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: center;
`;

export { LocationItemContainer, LocationItemImage, LocationItemInfo };
