import Link from 'next/link';
import styled from 'styled-components';

const LocationItemContainer = styled.div`
  box-shadow: 2px 2px 10px rgba(29, 99, 234, 0.48);
  background: #312a2a;
  width: 100%;
  height: 145px;
  margin: 11px;

  @media (min-width: 768px) {
    width: 396px;
  }
`;

export { LocationItemContainer };
