import styled from "styled-components"

const LoaderContainer = styled.div`
	width: 100%;

	@media(min-width: 768px){
		width: 200px;
		height: 100px;
		border-radius: 50%;
		overflow: hidden;
	}

	img{
		width: 100%
	}

`

export {
	LoaderContainer
}
