import React from 'react';
import { useQuery } from '@apollo/react-hooks';
import gql from 'graphql-tag';
import Link from 'next/link';

import { DetailsImage } from './styles';
import { Title, Text } from '../LocationsContainer/LocationItem/styles';
import { StyledLocationsContainer } from '../LocationsContainer/styles';

import { getLocationImage } from 'src/utils/helpers';

import ResidentItem from './ResidentItem';

const LOCATION_QUERY = gql`
  query Location($id: ID!) {
    location(id: $id) {
      name
      type
      id
      residents {
        image
        id
        name
        type
      }
    }
  }
`;

const LocationDetails = ({ id }) => {
  const { data, error, loading, fetchMore } = useQuery(LOCATION_QUERY, {
    variables: { id }
  });

  const loadMore = () => {
    if (data.location.residents.info.next) {
      fetchMore({
        variables: {
          page: data.location.residents.info.next
        },
        updateQuery: (prev, { fetchMoreResult }) => {
          if (!fetchMoreResult) return prev;
          return {
            locations: {
              ...prev.residents,
              info: { ...fetchMoreResult.residents.info },
              results: prev.locations.residents.concat(
                fetchMoreResult.residents.results
              )
            }
          };
        }
      });
    }
    return;
  };

  return (
    <>
      {data && (
        <>
          <DetailsImage>
            <img
              src={getLocationImage(data.location.type)}
              alt={data.location.type}
            />
          </DetailsImage>
          <Title margin="45px 5px 5px 5px">{data.location.name}</Title>
          <Text margin="5px">{data.location.type}</Text>
          <Title textAlign="center" margin="25px 0 0 0 ">
            Residents
          </Title>
          <StyledLocationsContainer>
            {data &&
              data.location.residents.map(resident => (
                <Link href={`/residents/${resident.id}`} key={resident.id}>
                  <a>
                    <ResidentItem
                      resident={resident}
                      locationType={data.location.type}
                    />
                  </a>
                </Link>
              ))}
          </StyledLocationsContainer>
        </>
      )}
    </>
  );
};

export default LocationDetails;
