import styled from 'styled-components';

const LoaderContainer = styled.div`
  @media (min-width: 768px) {
    width: 200px;
    height: 100px;
    img {
      border-radius: 50%;
    }
  }

  min-width: 200px;
  min-height: 100px;

  img {
    width: 100%;
  }
`;

export { LoaderContainer };
