import styled from 'styled-components';

import { stringSizes } from 'src/utils/mediaQueries';

const StyledHeader = styled.header`
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: flex-start;
	padding: 2px;
	overflow: hidden;

	img{
		width: 414px;
	}

  @media (min-width: ${stringSizes.phone}) {
    justify-content: center;
		img{
			width: 100%;
		}

  }
`;

export { StyledHeader };
