import React from 'react';
import Link from 'next/link';
import { useQuery } from '@apollo/react-hooks';
import gql from 'graphql-tag';

import { StyledLocationsContainer, ErrorMessage } from './styles';

import Loader from 'src/components/Loader';
import LocationItem from './LocationItem';
import { useInfiniteScroll } from 'src/hooks/useInfiniteScroll';

const LOCATIONS_QUERY = gql`
  query Locations($page: Int!) {
    locations(page: $page) {
      info {
        count
        pages
        next
        prev
      }
      results {
        name
        type
        id
      }
    }
  }
`;

const LocationsContainer = () => {
  const [currentPage, setCurrentPage] = React.useState(0);
  const { data, error, refetch, loading } = useQuery(LOCATIONS_QUERY, {
    variables: { page: currentPage }
  });
  const [locations, setLocations] = React.useState([]);

  React.useEffect(() => {
    setLocations(prevState => prevState.concat(data && data.locations.results));
  }, [data]);

  const loadMore = () => {
    if (data.locations.info.next) {
      setCurrentPage(prev => (prev += 1));
      refetch();
      // setLocations(
      //   prevState => data && [...prevState, ...data.locations.results]
      // );
    }
    return;
  };
  useInfiniteScroll(loadMore);

  return (
    <>
      <StyledLocationsContainer>
        {error && <ErrorMessage />}
        {locations &&
          locations.map(location => (
            <Link href={`/locations/${location.id}`} key={location.id}>
              <a>
                <LocationItem location={location} />
              </a>
            </Link>
          ))}
        {loading && <Loader />}
      </StyledLocationsContainer>
    </>
  );
};

export default LocationsContainer;
